        this.childComp.addEventListener('transitionend', this._trsend)
        if (['up', 'upLeft', 'upRight'].indexOf(this.getData('pos')) != -1) {
            this.childComp.style.top = parseInt(this.childComp.style.top) - this._hgt + 'px';
        }
