			lyteContent.style["overflow-y"] = "hidden";
			lyteContent.style.padding = "0px 0px";
			this.selectedOptionOperation();
			this.disableOnoptionsOperation();
			//var freezeLayer = component.childComp.querySelector("lyte-modal-freeze");
