store.registerModel("view",{
	id : Lyte.attr("string"),
	name : Lyte.attr("string"),
	fields : Lyte.attr("array"),
	selectedFields :Lyte.attr("array"),
	sortBy : Lyte.attr("string"),
	navigation : Lyte.belongsTo("navigation"),
	sortOrder : Lyte.attr("string",{pattern:/desc|asc/}),
	invokeSave : function(){
		this.$.save();
	}.observes("sortBy","sortOrder","selectedFields")
});